mapper.update:
update yellow_movie set
id = #{id}, title = #{title}, tags = #{tags}, imgUrl = #{imgUrl},
externalLink = #{externalLink}, internalLink = #{internalLink},
filePath = #{filePath}, createTime = #{createTime}
where id = #{id}

mapper.select:
select id, title, tags, imgUrl, externalLink, internalLink, filePath, createTime from yellow_movie

	where


			${obj.field} ${obj.operator} #{obj.value}
			${obj.join} ${obj.field} ${obj.operator} #{obj.value}





	order by

		${obj}



limit 0, 1

