mapper.update:
update yellow_movie_watch_record set
userId = #{userId}, ip = #{ip}, movieId = #{movieId},
lastWatchTime = #{lastWatchTime}, createTime = #{createTime}
where id = #{id}

mapper.select:
select * from yellow_movie_watch_record

	where


			${obj.field} ${obj.operator} #{obj.value}
			${obj.join} ${obj.field} ${obj.operator} #{obj.value}





	order by

		${obj}



limit 0, 1

